package com.psl.eltp.model;

import java.time.LocalDate;
import java.util.regex.*;

public class Contact {
	private String firstName, middleName, lastName;
	private LocalDate dob, anniversary;
	private String gender; 
	private String address, area, city, zip, state, country;
	private String telephone, mobile, email;
	public Contact() {
		super();
		// TODO Auto-generated constructor stub
	}
	public String getFirstName() {
		return firstName;
	}
	public void setFirstName(String firstName) {
		this.firstName = firstName;
	}
	public String getMiddleName() {
		return middleName;
	}
	public void setMiddleName(String middleName) {
		this.middleName = middleName;
	}
	public String getLastName() {
		return lastName;
	}
	public void setLastName(String lastName) {
		this.lastName = lastName;
	}
	public LocalDate getDob() {
		return dob;
	}
	public void setDob(LocalDate dob) {
		this.dob = dob;
	}
	public LocalDate getAnniversary() {
		return anniversary;
	}
	public void setAnniversary(LocalDate anniversary) {
		this.anniversary = anniversary;
	}
	public String getGender() {
		return gender;
	}
	public void setGender(String gender) {
		this.gender = gender;
	}
	public String getAddress() {
		return address;
	}
	public void setAddress(String address) {
		this.address = address;
	}
	public String getArea() {
		return area;
	}
	public void setArea(String area) {
		this.area = area;
	}
	public String getCity() {
		return city;
	}
	public void setCity(String city) {
		this.city = city;
	}
	public String getZip() {
		return zip;
	}
	public void setZip(String zip) {
		this.zip = zip;
	}
	public String getState() {
		return state;
	}
	public void setState(String state) {
		this.state = state;
	}
	public String getCountry() {
		return country;
	}
	public void setCountry(String country) {
		this.country = country;
	}
	public String getTelephone() {
		return telephone;
	}
	public void setTelephone(String telephone) {
		this.telephone = telephone;
	}
	public String getMobile() {
		return mobile;
	}
	public void setMobile(String mobile) {
		this.mobile = mobile;
	}
	public String getEmail() {
		return email;
	}
	public void setEmail(String email) {
		this.email = email;
	}
	public void validate() throws RequiredFieldException, InvalidFormatException {
		if(this.firstName==null || this.lastName == null || this.dob==null || this.email == null) {
			throw new RequiredFieldException("Please enter all compulsory fileds!");
		}
		
		String emailRegex = "^[a-zA-Z0-9_+&*-]+(?:\\."+ 
                "[a-zA-Z0-9_+&*-]+)*@" + 
                "(?:[a-zA-Z0-9-]+\\.)+[a-z" + 
                "A-Z]{2,7}$"; 
                  
		Pattern pat = Pattern.compile(emailRegex); 
		if (!pat.matcher(email).matches()) {
			throw new InvalidFormatException("Please enter a valid email address!");
		}
		if(this.telephone==null && this.mobile==null) {
			throw new RequiredFieldException("Please enter either of the contact numbers!");
		}
	}
}
